template_styles:
*{
    font-size: 12px;
    padding:0;
    margin:0;
}
h1,h2,h3,h4,h5,h6,p{
    padding:0;
    margin:0;
}
#wrapper{

    margin:20px;
}
.clr{
    clear:both;
}
/******* HEADER **********/
.header-left,.header-right,.footer-left{
    width:50%;
    float:left;
}
img{
    margin-top:30px;
}
.business-information{
    font-size: 12px;
    margin-top:5px;
}
.info{
    font-size: 12px;
    margin-left:30px;
    padding-top:70px;
}

/******* END HEADER **********/

/********** CONTENT **********/
.billing-address,.shipping-address,.payment-method,.shipping-method{
    font-size: 12px;
    float:left;
    width:50%;
    color:black;
}
.billing-address p,.shipping-address p,.payment-method p,.shipping-method p{
    font-size: 12px;
    margin-left:5px;
    margin-top:10px;
    margin-bottom:10px;
}
.content{
    font-size: 12px;
    padding-top:30px;
}
.mp-block-heading{
    font-size: 12px;
    width: 98% !important;
    padding: 5px 0px 5px 5px;
    color: #353535;
}

.mp-block-heading,.mp-note,.mp-heading{
    font-size: 12px;
    background-color: white;
}

/* Css table items */
.table{
  font-size: 12px;
    width:100%;
}
.header-table{
   font-size: 12px;
    width:100%;
    height:100%;
}
.mp-item{
   font-size: 12px;
    width:59%;
    float:left;
    padding:6px 0px;
}
.mp-qty,.mp-price,.mp-subtotal{
   font-size: 12px;
    width:13%;
    float:left;
    text-align:center;
    padding:6px 0px;

}
.mp-heading{
   font-size: 12px;
    width:100%;
    height:32px;
    color:black;
}
.mp-heading span{
    font-size: 12px;
    font-weight:bold;
}
.mp-item{
    font-size: 12px;
    padding-left:5px;
    font-size: 12px;
}
.order-totals{
    font-size: 12px;
    text-align: right;
    margin-top:20px;
}
.mp{
    font-size: 12px;
    width: 100%;
    height: 35px;
}
.mp-str{
    font-size: 12px;
    font-weight: bold;
    width: 80%;
    float: left;
}
.mp-right{
    font-size: 12px;
    width: 18%;
    float: left;
}
/* End css table items*/

/********** END CONTENT **********/

footer{
    margin-top:30px;
}
.footer-left{
    width:50%;
}
.mp-note{
    color:black;
    padding-left: 3px;
}
